        public List<String> PokazWersetyBibli(String nr_ksiegi, String nr_rozdzialu)
            {
                openDatabase();
                String[] ksiega = {nr_ksiegi, nr_rozdzialu};
                String mySQL = "select Verse,Scripture from bible where book = ? AND chapter = ?";
                Cursor k = mDatabase.rawQuery(mySQL, ksiega);
                List<String> ListaWersetow = new ArrayList<>();
                k.moveToFirst();
                while (!k.isAfterLast()) {
                    String numerwersu_cur = k.getString(0);
                    String trescwersu_cur = k.getString(1);
                    ListaWersetow.add(kolor_czcionki_rozdzial + numerwersu_cur + "." + "</font>" + kolor_czcionki_werset + "&nbsp;" + trescwersu_cur + "</font>");
                    k.moveToNext();
                }
                k.close();
                closeDatabase();
                return ListaWersetow;
            }
        public List<String> PokazTematyKomentarzy(int WybierzZakres)
            {
                openDatabase();
                ArrayList<String> Ksiegi = new ArrayList<>();
                Cursor k = mDatabase.rawQuery("select * from Elementy_Menu_NazwyKsiag_NumeryRozdzialow", null);
                k.moveToFirst();
                while (!k.isAfterLast()) {
                    String wartosc_z_kolumny = k.getString(WybierzZakres);
                    if (wartosc_z_kolumny == null) {
                        break;
                    }
                    else {
                        Ksiegi.add(wartosc_z_kolumny);
                        k.moveToNext();
                    }
                }
                k.close();
                closeDatabase();
                return Ksiegi;
            }
